{"cells":[{"cell_type":"code","source":["import spacy\n","import os\n","from pathlib import Path\n","from flask import Flask, request, jsonify\n","\n","# Flask 앱 생성\n","app = Flask(__name__)\n","\n","# Google Drive에서 모델 다운로드 URL\n","MODEL_URL = \"https://drive.google.com/uc?id=YOUR_MODEL_FILE_ID\"  # 모델 파일 ID로 수정 필요\n","\n","# 모델 저장 경로\n","MODEL_PATH = \"./model-best\"\n","\n","# 모델 다운로드 및 로드\n","if not Path(MODEL_PATH).exists():\n","    print(\"Custom Spacy model not found. Downloading from Google Drive...\")\n","    os.system(f\"wget -O model-best.zip {MODEL_URL}\")  # 모델 ZIP 파일 다운로드\n","    os.system(\"unzip model-best.zip -d ./\")          # ZIP 파일 해제\n","    os.system(\"rm model-best.zip\")                  # ZIP 파일 삭제\n","\n","# Spacy 모델 로드\n","nlp = spacy.load(MODEL_PATH)\n","\n","# API 엔드포인트 정의\n","@app.route('/predict', methods=['POST'])\n","def predict():\n","    data = request.json\n","    document = data.get(\"text\", \"\")\n","\n","    if not document:\n","        return jsonify({\"error\": \"No text provided\"}), 400\n","\n","    # Spacy 모델로 예측\n","    doc = nlp(document)\n","    category_scores = doc.cats\n","    sorted_categories = sorted(category_scores.items(), key=lambda item: item[1], reverse=True)\n","\n","    # 결과 반환\n","    return jsonify({\n","        \"predictions\": [\n","            {\"category\": category, \"score\": score}\n","            for category, score in sorted_categories\n","        ]\n","    })\n","\n","# 기본 라우트\n","@app.route('/')\n","def home():\n","    return \"Welcome to the Flask-Spacy API!\"\n","\n","# Flask 서버 실행\n","if __name__ == '__main__':\n","    port = int(os.environ.get(\"PORT\", 5000))  # Render의 PORT 환경 변수 사용\n","    app.run(host='0.0.0.0', port=port)"],"metadata":{"id":"Sa-oM95KSOu6"},"execution_count":null,"outputs":[]}],"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyOf1iTDx1hcuW+k6IOqZPk9"},"kernelspec":{"display_name":"Python 3","name":"python3"},"language_info":{"name":"python"}},"nbformat":4,"nbformat_minor":0}